function init() {

  var viewportWidth = Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
  var viewportHeight = Math.max(document.documentElement.clientHeight, window.innerHeight || 0);
  var mobile = false;
  if(viewportHeight<709){
    mobile = true;
  }

  var dataSet = "drake";
  var dataToEnter = drake;
  var w = 709
  var h = 406
  var changeInterval
  var scale = 1.5
  let containerSelector = '.animation';
  var container = d3.select(containerSelector);
  var svg = container.append("svg")
    // .attr("width",w/scale)
    // .attr("height",h/scale)
    .style("width","100%")
    // .style("height",h/scale+"px")
    .style("width",function(d){
      if(mobile){
        return "100%";
      }
      return w/scale+"px"
    })
    // .style("height",h/scale+"px")
    .attr("viewBox","0 0 709 406")
    .append("g")

  var triangle = svg
    .selectAll("path")
    .data(dataToEnter)
    .enter()
    .append("path")
    .attr("d", function(d,i){
      var newCoor = [];
      d.triangle.forEach(function(item){
        var element = item.map(function(num){
          return Math.floor(num);
        });
        newCoor.push(element);
      });
      return "M" + newCoor.join("L") + "Z";
    })
    .attr("fill", function(d){
      return d.color;
    })
    .attr("stroke", function (d){
      return d.color;
    })
    .on("mouseover",function(d){
    })
    .on("click",function(d){
      changeInterval.stop();
      // changeInterval = d3.interval(change,4000)
    })
    ;

    function change(){

      if(dataSet == "drake"){
        dataSet = "elvis"
        dataToEnter = elvis
      }
      else if(dataSet == "elvis"){
        dataSet = "bowie"
        dataToEnter = bowie
      }
      else{
        dataSet = "drake"
        dataToEnter = drake
      }

      triangle = svg.selectAll("path").data(dataToEnter);

      triangle.enter().append("path")
        .attr("fill", function(d){
          return d.color;
        })
        .attr("stroke", function(d){
          return d.color;
        })
        .transition()
        .delay(function(d, i) { return i * 2; })
        .duration(1000)
        .attr("d", function(d,i){
          return "M" + d.triangle.join("L") + "Z";
        })
        ;

      triangle.transition()
         .delay(function(d, i) { return i * 2; })
         .duration(1000)
         //.ease("cubic-in-out")
         .attr("d", function(d,i){
           return "M" + d.triangle.join("L") + "Z";
         })
         .attr("fill", function(d){
           return d.color;
         })
         .attr("stroke", function(d){
           return d.color;
         })
    //     .attr("transform", "translate(0,600)")
         ;

      triangle.exit().transition()
        .delay(function(d, i) { return i * 1; })
        .duration(200)
        .attr("d", function(d,i){
          return "M" + d.triangle.join("L") + "Z";
        })
        .remove();
    }

    changeInterval = d3.interval(change,4000);

    let scene = new ScrollMagic.Scene({
      triggerElement: containerSelector,
      triggerHook: 'onLeave',
      offset: container.node().offsetHeight
    })
    .on('enter', () => {
      //console.log('Turning off polygon animation');
      changeInterval.stop();
    })
    .on('leave', () => {
      //console.log('Re-enabling polygon animation');
      changeInterval = d3.interval(change,4000);
    })
    .addTo(scroll_controller);

}

export default { init };
